    createToast(toast, icon, text){
      toast.innerHTML = `<div class="column">
                         <i class="fa-solid ${icon}"></i>
                         <span>${text}</span>
                      </div>
                      <i class="fa-solid fa-xmark" id="close-toast-btn"></i>`;
    }

    async setActiveButton(check){
      const loginbtn = this.root.querySelector('#qsLoginBtn');
      const logoutbtn = this.root.querySelector('#qsLogoutBtn');
      const response = await this.onAuthVerification();
      console.log(response);
      if(response === true){
        loginbtn.style.display = 'none';
        logoutbtn.style.display = 'flex';
      }
      else if(response === false){
        loginbtn.style.display = 'flex';
        logoutbtn.style.display = 'none';
      }
    }

    updateWeatherSec(weather){
      try{
        const mainCol = this.root.querySelector('.second-sec');

        const mainWrapper = this.root.querySelector('.main-col-wrapper');
  
        mainCol.innerHTML = "";
  
        const html = this._createWeatherSecHTML(weather.name, weather.main.temp, weather.weather[0].main, weather.main.humidity, weather.wind.speed);
  
        mainCol.insertAdjacentHTML("beforeend", html);

        mainCol.classList.remove('closed');
        mainWrapper.style.gap = "60px";
      }
      catch (e){
        console.log(e);
      }
    }

    removeToast(toast){
      toast.classList.add("hide");
      if(toast.timeoutId) clearTimeout(toast.timeoutId);
      setTimeout(() => toast.remove(), 500);
    }


    updateToast(id){
        const toastDetails = {
          timer: 5000,
          success: {
              icon: 'fa-circle-check',
              text: 'Success: This is a success toast.',
          },
          error: {
              icon: 'fa-circle-xmark',
              text: 'Error: Something Went Wrong.',
          },
          warning: {
              icon: 'fa-triangle-exclamation',
              text: 'Warning: City Not Found.',
          },
          info: {
              icon: 'fa-circle-info',
              text: 'Info: This is an information toast.',
          },
          notLogged:{
            icon: 'fa-triangle-exclamation',
            text: 'Warning: Not Logged In.'
          }
        }
        const { icon, text } = toastDetails[id];
        const notifications = this.root.querySelector('#toast-notifications');
        const toast = document.createElement("li");
        toast.className = `toast ${id}`;

        this.createToast(toast, icon, text);
        
        notifications.appendChild(toast);

        const closebtn = toast.querySelector('#close-toast-btn');

        closebtn.addEventListener('click', (e) => {
          this.removeToast(toast);
        });

        toast.timeoutId = setTimeout(() => this.removeToast(toast), toastDetails.timer);
    }
